Sample DEFINITIONS ::= BEGIN

    Question ::= SEQUENCE {
        trackingNumber INTEGER,
        question       IA5String
    }

    Answer ::= SEQUENCE {
        questionNumber INTEGER,
        answer         BOOLEAN
    }

END

Sample DEFINITIONS ::= BEGIN

    Question ::= SEQUENCE {
        trackingNumber INTEGER(0..199),
        question       IA5String
    }

    Answer ::= SEQUENCE {
        questionNumber INTEGER(10..20),
        answer         BOOLEAN
    }

    History ::= SEQUENCE {
        questions SEQUENCE(SIZE(0..10)) OF Question,
        answers   SEQUENCE(SIZE(1..10)) OF Answer,
        anArray   SEQUENCE(SIZE(100))  OF INTEGER(0..1000),
    }

END
